import React, { useState, useEffect } from "react";

const AgregarLibro = () => {
  const [formData, setFormData] = useState({
    isbn: "",
    titulo: "",
    autor: "",
    editorial: "",
    stock: 0,
    precio: 0,
    ubicacion: "",
  });

  const [mensaje, setMensaje] = useState("");

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
    setMensaje(""); // Borrar mensaje al editar
  };

  const handleAutocomplete = async () => {
    const { isbn } = formData;

    if (isbn) {
      try {
        const response = await fetch(
          `http://localhost:5000/libros?isbn=${isbn}`
        );
        const data = await response.json();

        if (response.ok && data.length > 0) {
          const libro = data[0];

          setFormData({
            isbn: isbn,
            titulo: libro.titulo || "",
            autor: libro.autor || "",
            editorial: libro.editorial || "",
            stock: libro.stock || 0,
            precio: libro.precio || 0,
            ubicacion: libro.ubicacion || "",
          });
        } else {
          setFormData({
            isbn: formData.isbn,
            titulo: "",
            autor: "",
            editorial: "",
            stock: 0,
            precio: 0,
            ubicacion: "",
          });
        }
      } catch (error) {
        console.error("Error al autocompletar los datos:", error);
        alert("Error al intentar obtener los datos del libro.");
      }
    }
  };

  useEffect(() => {
    if (!formData.isbn) {
      setFormData({
        isbn: "",
        titulo: "",
        autor: "",
        editorial: "",
        stock: 0,
        precio: 0,
        ubicacion: "",
      });
    } else {
      handleAutocomplete();
    }
  }, [formData.isbn]);

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!formData.isbn || !formData.titulo || !formData.autor) {
      alert("Por favor, complete todos los campos obligatorios.");
      return;
    }

    if (formData.stock < 0 || formData.precio < 0) {
      alert("El stock y el precio no pueden ser negativos.");
      return;
    }

    try {
      const response = await fetch(
        `http://localhost:5000/libros?isbn=${formData.isbn}`
      );
      const data = await response.json();

      if (response.ok && data.length > 0) {
        const libroExistente = data[0];

        let cambios = [];

        if (formData.titulo !== libroExistente.titulo) cambios.push("título");
        if (formData.autor !== libroExistente.autor) cambios.push("autor");
        if (formData.editorial !== libroExistente.editorial)
          cambios.push("editorial");
        if (formData.stock !== libroExistente.stock) cambios.push("stock");
        if (formData.precio !== libroExistente.precio) cambios.push("precio");
        if (formData.ubicacion !== libroExistente.ubicacion)
          cambios.push("ubicación");

        if (cambios.length > 0) {
          // SI hay cambios → HACEMOS PUT
          const responseUpdate = await fetch(
            `http://localhost:5000/libros/${libroExistente.id}`,
            {
              method: "PUT",
              headers: {
                "Content-Type": "application/json",
              },
              body: JSON.stringify(formData),
            }
          );

          if (responseUpdate.ok) {
            setMensaje(
              `Libro actualizado con éxito. Campos modificados: ${cambios.join(
                ", "
              )}.`
            );
          } else {
            alert("Error al actualizar el libro.");
          }
        } else {
          // Si NO hubo cambios
          setMensaje("No se realizaron cambios en el libro.");
        }
      } else {
        // Si el libro es nuevo → HACEMOS POST
        const responseCrear = await fetch("http://localhost:5000/libros", {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify(formData),
        });

        if (responseCrear.ok) {
          setMensaje("Libro creado con éxito.");
          setFormData({
            isbn: "",
            titulo: "",
            autor: "",
            editorial: "",
            stock: 0,
            precio: 0,
            ubicacion: "",
          });
        } else {
          const dataError = await responseCrear.json();
          alert(dataError.error || "Hubo un error al crear el libro.");
        }
      }
    } catch (error) {
      console.error("Error en la solicitud:", error);
      alert("Error con la solicitud: " + error.message);
    }
  };

  const limpiarFormulario = () => {
    setFormData({
      isbn: "",
      titulo: "",
      autor: "",
      editorial: "",
      stock: 0,
      precio: 0,
      ubicacion: "",
    });
    setMensaje("");
  };

  return (
    <div className="container mt-5">
      <div className="card shadow-lg p-4">
        <h2 className="mb-4 text-center">Crear o Editar Libro</h2>
        {mensaje && <div className="alert alert-info">{mensaje}</div>}
        <form onSubmit={handleSubmit}>
          <div className="mb-3">
            <label htmlFor="isbn" className="form-label">
              ISBN:
            </label>
            <input
              type="text"
              className="form-control"
              id="isbn"
              name="isbn"
              value={formData.isbn}
              onChange={handleChange}
              required
            />
          </div>

          <div className="mb-3">
            <label htmlFor="titulo" className="form-label">
              Título:
            </label>
            <input
              type="text"
              className="form-control"
              id="titulo"
              name="titulo"
              value={formData.titulo}
              onChange={handleChange}
              required
            />
          </div>

          <div className="mb-3">
            <label htmlFor="autor" className="form-label">
              Autor:
            </label>
            <input
              type="text"
              className="form-control"
              id="autor"
              name="autor"
              value={formData.autor}
              onChange={handleChange}
              required
            />
          </div>

          <div className="mb-3">
            <label htmlFor="editorial" className="form-label">
              Editorial:
            </label>
            <input
              type="text"
              className="form-control"
              id="editorial"
              name="editorial"
              value={formData.editorial}
              onChange={handleChange}
            />
          </div>

          <div className="mb-3">
            <label htmlFor="stock" className="form-label">
              Stock:
            </label>
            <input
              type="number"
              className="form-control"
              id="stock"
              name="stock"
              value={formData.stock}
              onChange={handleChange}
            />
          </div>

          <div className="mb-3">
            <label htmlFor="precio" className="form-label">
              Precio:
            </label>
            <input
              type="number"
              className="form-control"
              id="precio"
              name="precio"
              value={formData.precio}
              onChange={handleChange}
            />
          </div>

          <div className="mb-3">
            <label htmlFor="ubicacion" className="form-label">
              Ubicación:
            </label>
            <input
              type="text"
              className="form-control"
              id="ubicacion"
              name="ubicacion"
              value={formData.ubicacion}
              onChange={handleChange}
            />
          </div>

          <div className="row mt-4">
            <div className="col-9">
              <button type="submit" className="btn btn-primary btn-lg w-100">
                Guardar
              </button>
            </div>
            <div className="col-3">
              <button
                type="button"
                className="btn btn-warning btn-lg w-100"
                onClick={limpiarFormulario}
              >
                Limpiar
              </button>
            </div>
          </div>
        </form>
      </div>
    </div>
  );
};

export default AgregarLibro;
