import React, { useState, useEffect } from "react";

const AgregarLibro = () => {
  const [formData, setFormData] = useState({
    isbn: "",
    titulo: "",
    autor: "",
    editorial: "",
    stock: 0,
    precio: 0,
    ubicacion: "",
  });

  const [mensaje, setMensaje] = useState(""); // Estado para el mensaje de éxito
  const [editando, setEditando] = useState(false); // Estado para saber si estamos editando un libro

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
    setMensaje(""); // ← Esto borra el mensaje al editar
  };

  const handleAutocomplete = async () => {
    const { isbn } = formData;

    if (isbn) {
      try {
        const response = await fetch(
          `http://localhost:5000/libros?isbn=${isbn}`
        );
        const data = await response.json();

        if (response.ok && data.length > 0) {
          const libro = data[0];

          setFormData({
            ...formData,
            autor: libro.autor || "",
            editorial: libro.editorial || "",
            stock: libro.stock || 0,
            precio: libro.precio || 0,
            titulo: libro.titulo || "",
            ubicacion: libro.ubicacion || "",
          });

          setEditando(true); // Si encontramos el libro, activamos el modo de edición
        } else {
          setFormData({
            isbn: formData.isbn,
            titulo: "",
            autor: "",
            editorial: "",
            stock: 0,
            precio: 0,
            ubicacion: "",
          });

          setEditando(false); // Si no encontramos el libro, estamos en modo de creación
        }
      } catch (error) {
        console.error("Error al autocompletar los datos:", error);
        alert("Hubo un error al intentar obtener los datos del libro.");
      }
    }
  };

  useEffect(() => {
    if (!formData.isbn) {
      setFormData({
        isbn: "",
        titulo: "",
        autor: "",
        editorial: "",
        stock: 0,
        precio: 0,
        ubicacion: "",
      });
      setEditando(false); // Reseteamos el estado de edición si no hay ISBN
    } else {
      handleAutocomplete();
    }
  }, [formData.isbn]);

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!formData.isbn || !formData.titulo || !formData.autor) {
      alert("Por favor, complete todos los campos obligatorios.");
      return;
    }

    if (formData.stock < 0 || formData.precio < 0) {
      alert("El stock y el precio no pueden ser negativos.");
      return;
    }

    try {
      let response;
      let data;

      if (editando) {
        // Si estamos editando, hacemos un PUT para actualizar el libro
        response = await fetch(
          `http://localhost:5000/libros/${formData.isbn}`,
          {
            method: "PUT",
            headers: {
              "Content-Type": "application/json",
            },
            body: JSON.stringify(formData),
          }
        );
      } else {
        // Si estamos creando, hacemos un POST para agregar el libro
        response = await fetch("http://localhost:5000/libros", {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify(formData),
        });
      }

      data = await response.json();

      if (response.ok) {
        if (editando) {
          setMensaje("Libro actualizado con éxito.");
        } else {
          setMensaje("Libro creado con éxito.");
        }

        setFormData({
          isbn: "",
          titulo: "",
          autor: "",
          editorial: "",
          stock: 0,
          precio: 0,
          ubicacion: "",
        });
        setEditando(false); // Resetear el modo de edición
      } else {
        alert(data.error || "Hubo un error al guardar el libro");
      }
    } catch (error) {
      console.error("Error en la solicitud:", error);
      alert("Hubo un error con la solicitud: " + error.message);
    }
  };

  return (
    <div className="container mt-5">
      <div className="card shadow-lg p-4">
        <h2 className="mb-4 text-center">
          {editando ? "Editar Libro" : "Crear Nuevo Libro"}
        </h2>
        {mensaje && <div className="alert alert-info">{mensaje}</div>}
        <form onSubmit={handleSubmit}>
          <div className="mb-3">
            <label htmlFor="isbn" className="form-label">
              ISBN:
            </label>
            <input
              type="text"
              className="form-control"
              id="isbn"
              name="isbn"
              value={formData.isbn}
              onChange={handleChange}
              required
            />
          </div>

          <div className="mb-3">
            <label htmlFor="titulo" className="form-label">
              Título:
            </label>
            <input
              type="text"
              className="form-control"
              id="titulo"
              name="titulo"
              value={formData.titulo}
              onChange={handleChange}
              required
            />
          </div>

          <div className="mb-3">
            <label htmlFor="autor" className="form-label">
              Autor:
            </label>
            <input
              type="text"
              className="form-control"
              id="autor"
              name="autor"
              value={formData.autor}
              onChange={handleChange}
              required
            />
          </div>

          <div className="mb-3">
            <label htmlFor="editorial" className="form-label">
              Editorial:
            </label>
            <input
              type="text"
              className="form-control"
              id="editorial"
              name="editorial"
              value={formData.editorial}
              onChange={handleChange}
            />
          </div>

          <div className="mb-3">
            <label htmlFor="stock" className="form-label">
              Stock:
            </label>
            <input
              type="number"
              className="form-control"
              id="stock"
              name="stock"
              value={formData.stock}
              onChange={handleChange}
            />
          </div>

          <div className="mb-3">
            <label htmlFor="precio" className="form-label">
              Precio:
            </label>
            <input
              type="number"
              className="form-control"
              id="precio"
              name="precio"
              value={formData.precio}
              onChange={handleChange}
            />
          </div>

          <div className="mb-3">
            <label htmlFor="ubicacion" className="form-label">
              Ubicación:
            </label>
            <input
              type="text"
              className="form-control"
              id="ubicacion"
              name="ubicacion"
              value={formData.ubicacion}
              onChange={handleChange}
            />
          </div>

          <div className="row mt-4">
            <div className="col-9">
              <button type="submit" className="btn btn-primary btn-lg w-100">
                {editando ? "Actualizar Libro" : "Crear Libro"}
              </button>
            </div>
            <div className="col-3">
              <button
                type="button"
                className="btn btn-warning btn-lg w-100"
                onClick={() => {
                  setFormData({
                    isbn: "",
                    titulo: "",
                    autor: "",
                    editorial: "",
                    stock: 0,
                    precio: 0,
                    ubicacion: "",
                  });
                  setMensaje(""); // ← Esto borra el mensaje
                  setEditando(false); // Resetear modo de edición
                }}
              >
                Refrescar
              </button>
            </div>
          </div>
        </form>
      </div>
    </div>
  );
};

export default AgregarLibro;
