import React, { useState } from "react";
import { useAppContext } from "../context/appContext";
import { useNavigate } from "react-router-dom";

const AgregarLibro = () => {
  const handleInputKeyDown = (e) => {
    if (e.key === "Enter") {
      e.preventDefault();
      const form = e.target.form;
      const index = Array.prototype.indexOf.call(form, e.target);
      if (form.elements[index + 1]) {
        form.elements[index + 1].focus();
      }
    }
  };

  const { store, actions } = useAppContext();
  const navigate = useNavigate();

  const [formData, setFormData] = useState({
    isbn: "",
    titulo: "",
    autor: "",
    editorial: "",
    stock: 1,
    precio: 0,
    ubicacion: "",
  });

  const [sinIsbn, setSinIsbn] = useState(false);
  const [isbnGenerado, setIsbnGenerado] = useState("");
  const [isLoading, setIsLoading] = useState(false);
  const [datosCargados, setDatosCargados] = useState(false);
  const [origen, setOrigen] = useState("");
  const [generandoIsbn, setGenerandoIsbn] = useState(false);

  const mensaje = store.mensaje;

  const limpiarDatosLibro = (isbnValue = formData.isbn) => {
    setFormData({
      isbn: isbnValue,
      titulo: "",
      autor: "",
      editorial: "",
      stock: 1,
      precio: 0,
      ubicacion: "",
    });
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });

    if (name === "isbn") {
      setOrigen("");
      setDatosCargados(false);
      limpiarDatosLibro(value);
    }

    actions.setMensaje("");
  };

  const generarYMostrarIsbn = async () => {
    try {
      setGenerandoIsbn(true);
      actions.setMensaje("Generando ISBN automáticamente...");

      const resultado = await actions.generarIsbnAutomatico();

      if (resultado.success) {
        setIsbnGenerado(resultado.isbn);
        setFormData({ ...formData, isbn: resultado.isbn });
        actions.setMensaje(`✅ ISBN generado automáticamente: ${resultado.isbn}`);
        setOrigen("servidor");
      } else {
        actions.setMensaje(`❌ ${resultado.error}`);
        setOrigen("");
        setSinIsbn(false);
        setFormData({ ...formData, isbn: "" });
      }
    } catch (error) {
      console.error("Error al generar ISBN:", error);
      actions.setMensaje("❌ Error inesperado al generar ISBN.");
      setOrigen("");
      setSinIsbn(false);
    } finally {
      setGenerandoIsbn(false);
    }
  };

  const handleAutocomplete = async () => {
    const { isbn } = formData;
    if (!isbn) return;

    setIsLoading(true);
    setOrigen("");

    try {
      const libroLocal = await actions.buscarLibroPorISBN(isbn);

      if (libroLocal) {
        setFormData({
          ...formData,
          autor: libroLocal.autor || "",
          editorial: libroLocal.editorial || "",
          stock: libroLocal.stock || 1,
          precio: libroLocal.precio || 0,
          titulo: libroLocal.titulo || "",
          ubicacion: libroLocal.ubicacion || "",
        });
        setOrigen("local");
        setDatosCargados(true);
      } else {
        const libroExterno = await actions.buscarLibroExterno(isbn);

        if (libroExterno) {
          setFormData({
            ...formData,
            titulo: libroExterno.titulo || "",
            autor: libroExterno.autor || "",
            editorial: libroExterno.editorial || "",
          });
          setOrigen("externo");
          setDatosCargados(true);
          actions.setMensaje(`Datos obtenidos de ${libroExterno.fuente || "fuente externa"}. Puede editar si es necesario.`);
        } else {
          actions.setMensaje("No se encontró información para este ISBN. Puede ingresar los datos manualmente.");
          setOrigen("");
          setDatosCargados(false);
          limpiarDatosLibro(isbn);
        }
      }
    } catch (error) {
      console.error("Error al autocompletar los datos:", error);
      actions.setMensaje("Hubo un error al buscar información del libro.");
    } finally {
      setIsLoading(false);
    }
  };

  const handleIsbnBlur = () => {
    if (formData.isbn && !datosCargados && !sinIsbn) {
      handleAutocomplete();
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const { isbn, titulo, autor, ubicacion, stock } = formData;

    if (!isbn || !titulo || !autor || !ubicacion) {
      alert("Por favor, complete los campos obligatorios: ISBN, título, autor y ubicación.");
      return;
    }

    if (stock < 1) {
      alert("El stock debe ser al menos 1.");
      return;
    }

    if (formData.precio < 0) {
      alert("El precio no puede ser negativo.");
      return;
    }

    try {
      const libroExistente = await actions.buscarLibroPorISBN(formData.isbn);

      if (libroExistente) {
        let cambios = [];

        if (formData.titulo !== libroExistente.titulo) cambios.push("título");
        if (formData.autor !== libroExistente.autor) cambios.push("autor");
        if (formData.editorial !== libroExistente.editorial) cambios.push("editorial");
        if (parseInt(formData.stock) !== parseInt(libroExistente.stock)) cambios.push(`stock (${libroExistente.stock} → ${formData.stock})`);
        if (parseFloat(formData.precio) !== parseFloat(libroExistente.precio)) cambios.push("precio");
        if (formData.ubicacion !== libroExistente.ubicacion) cambios.push("ubicación");

        if (cambios.length > 0) {
          const resultado = await actions.actualizarLibro(libroExistente.id, formData);
          if (resultado.success) {
            actions.setMensaje(`Libro actualizado con éxito. Campos modificados: ${cambios.join(", ")}.`);
            setTimeout(() => actions.setMensaje(""), 10000);
            setOrigen("local");
          } else {
            alert(resultado.error || "Hubo un error al actualizar el libro");
          }
        } else {
          actions.setMensaje("No se realizaron cambios en el libro.");
          setTimeout(() => actions.setMensaje(""), 10000);
        }
      } else {
        const resultado = await actions.crearLibro(formData);
        if (resultado.success) {
          actions.setMensaje(`Libro creado con éxito con stock de ${formData.stock} unidad(es).`);
          setTimeout(() => actions.setMensaje(""), 10000);
          setFormData({ isbn: "", titulo: "", autor: "", editorial: "", stock: 1, precio: 0, ubicacion: "" });
          setOrigen("");
          setDatosCargados(false);
          setSinIsbn(false);
          setIsbnGenerado("");
        } else {
          alert(resultado.error || "Hubo un error al crear el libro");
        }
      }
    } catch (error) {
      console.error("Error en la solicitud:", error);
      alert("Hubo un error con la solicitud: " + error.message);
    }
  };

  return (
    <div className="container mt-0 bg-success-subtle rounded-4">
      <div className="card shadow-lg p-4 border-0 rounded-4">
        <div className="d-flex justify-content-between align-items-center mb-3">
          <button
            type="button"
            className="btn btn-secondary"
            onClick={() => navigate("/")}
          >
            Volver al inicio
          </button>

          <h2 className="mb-0 text-center flex-grow-1">Crear Nuevo Libro</h2>
          {mensaje && (
            <div className="alert alert-info text-center" role="alert">
              {mensaje}
            </div>
          )}
          <div style={{ width: "130px" }}></div>
        </div>

        {/* FORMULARIO ORIGINAL, NO TOCADO */}
        <form onSubmit={handleSubmit}>
          {/* Checkbox para crear sin ISBN */}
          <div className="mb-3">
            <label htmlFor="isbn" className="form-label d-block">ISBN:</label>
            <div className="d-flex align-items-center gap-2">
              <div className="flex-grow-1">
                <div className="input-group">
                  <input
                    type="text"
                    className="form-control"
                    id="isbn"
                    name="isbn"
                    value={formData.isbn}
                    onChange={handleChange}
                    onBlur={handleIsbnBlur}
                    required
                    onKeyDown={handleInputKeyDown}
                    readOnly={sinIsbn}
                    placeholder={sinIsbn ? "Se generará automáticamente..." : "Ingrese el ISBN"}
                  />
                  {isLoading && !sinIsbn && (
                    <span className="input-group-text">
                      <div className="spinner-border spinner-border-sm" role="status">
                        <span className="visually-hidden">Cargando...</span>
                      </div>
                    </span>
                  )}
                  {!isLoading && origen && (
                    <span
                      className={`input-group-text ${origen === "local"
                        ? "bg-success text-white"
                        : origen === "servidor"
                          ? "bg-primary text-white"
                          : origen === "fallback"
                            ? "bg-warning text-dark"
                            : "bg-info text-white"
                        }`}
                    >
                      {origen === "local"
                        ? "BD Local"
                        : origen === "servidor"
                          ? "Servidor"
                          : origen === "fallback"
                            ? "Fallback"
                            : "API Externa"}
                    </span>
                  )}
                </div>
              </div>

              <div className="form-check ms-2">
                <input
                  type="checkbox"
                  className="form-check-input"
                  checked={sinIsbn}
                  onChange={async (e) => {
                    const checked = e.target.checked;
                    if (checked) {
                      await generarYMostrarIsbn();
                      setSinIsbn(true);
                      setDatosCargados(false);
                    } else {
                      setFormData({ ...formData, isbn: "" });
                      setIsbnGenerado("");
                      setOrigen("");
                      setDatosCargados(false);
                      setSinIsbn(false);
                      actions.setMensaje("");
                    }
                  }}
                  disabled={generandoIsbn}
                  id="crearSinIsbn"
                />
                <label htmlFor="crearSinIsbn" className="form-check-label small">
                  Crear sin ISBN
                </label>
                {generandoIsbn && (
                  <span className="ms-2">
                    <div className="spinner-border spinner-border-sm" role="status">
                      <span className="visually-hidden">Generando...</span>
                    </div>
                  </span>
                )}
              </div>
            </div>
            <small className="text-muted">
              {sinIsbn ? "ISBN generado automáticamente. Se asignará el próximo número disponible." : ""}
            </small>
          </div>

          {/* Resto de inputs */}
          {["titulo", "autor", "editorial", "stock", "precio", "ubicacion"].map((field, i) => (
            <div className="mb-3" key={i}>
              <label htmlFor={field} className="form-label">
                {field.charAt(0).toUpperCase() + field.slice(1)}{field === "stock" && " (mínimo 1)"}:
              </label>
              <input
                type={["stock", "precio"].includes(field) ? "number" : "text"}
                className="form-control"
                id={field}
                name={field}
                value={formData[field]}
                onChange={handleChange}
                onKeyDown={handleInputKeyDown}
                {...(field === "stock" ? { min: 1 } : {})}
              />
            </div>
          ))}

          <div className="row mt-4">
            <div className="col-9">
              <button type="submit" className="btn btn-success btn-lg w-100">
                {formData.id ? "Actualizar Libro" : "Crear Libro"}
              </button>
            </div>
            <div className="col-3">
              <button
                type="button"
                className="btn btn-warning btn-lg w-100"
                onClick={() => {
                  setFormData({ isbn: "", titulo: "", autor: "", editorial: "", stock: 1, precio: 0, ubicacion: "" });
                  setOrigen("");
                  setDatosCargados(false);
                  setSinIsbn(false);
                  setIsbnGenerado("");
                  actions.setMensaje("");
                }}
              >
                Refrescar
              </button>
            </div>
          </div>
        </form>
      </div>
    </div>
  );
};

export default AgregarLibro;
