import React, { useState } from "react";
import { useAppContext } from "../context/appContext";
import { useNavigate } from "react-router-dom";

const AgregarLibro = () => {
  const { store, actions } = useAppContext();
  const navigate = useNavigate();

  const [formData, setFormData] = useState({
    isbn: "",
    titulo: "",
    autor: "",
    editorial: "",
    stock: 1,
    precio: 0,
    ubicacion: "",
  });

  const [sinIsbn, setSinIsbn] = useState(false);
  const [isbnGenerado, setIsbnGenerado] = useState("");
  const [isLoading, setIsLoading] = useState(false);
  const [datosCargados, setDatosCargados] = useState(false);
  const [origen, setOrigen] = useState("");
  const [generandoIsbn, setGenerandoIsbn] = useState(false);

  const mensaje = store.mensaje;

  const limpiarDatosLibro = (isbnValue = formData.isbn) => {
    setFormData({
      isbn: isbnValue,
      titulo: "",
      autor: "",
      editorial: "",
      stock: 1,
      precio: 0,
      ubicacion: "",
    });
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });

    if (name === "isbn") {
      setOrigen("");
      setDatosCargados(false);
      limpiarDatosLibro(value);
    }

    actions.setMensaje("");
  };

  const generarYMostrarIsbn = async () => {
    try {
      setGenerandoIsbn(true);
      actions.setMensaje("Generando ISBN automáticamente...");

      const resultado = await actions.generarIsbnAutomatico();

      if (resultado.success) {
        setIsbnGenerado(resultado.isbn);
        setFormData({
          ...formData,
          isbn: resultado.isbn,
        });
        actions.setMensaje(`✅ ISBN generado automáticamente: ${resultado.isbn}`);
        setOrigen("servidor");
      } else {
        actions.setMensaje(`❌ ${resultado.error}`);
        setOrigen("");
        setSinIsbn(false);
        setFormData({
          ...formData,
          isbn: "",
        });
      }
    } catch (error) {
      console.error("Error al generar ISBN:", error);
      actions.setMensaje("❌ Error inesperado al generar ISBN.");
      setOrigen("");
      setSinIsbn(false);
    } finally {
      setGenerandoIsbn(false);
    }
  };

  const handleAutocomplete = async () => {
    const { isbn } = formData;
    if (!isbn) return;

    setIsLoading(true);
    setOrigen("");

    try {
      const libroLocal = await actions.buscarLibroPorISBN(isbn);

      if (libroLocal) {
        setFormData({
          ...formData,
          autor: libroLocal.autor || "",
          editorial: libroLocal.editorial || "",
          stock: libroLocal.stock || 1,
          precio: libroLocal.precio || 0,
          titulo: libroLocal.titulo || "",
          ubicacion: libroLocal.ubicacion || "",
        });
        setOrigen("local");
        setDatosCargados(true);
      } else {
        const libroExterno = await actions.buscarLibroExterno(isbn);

        if (libroExterno) {
          setFormData({
            ...formData,
            titulo: libroExterno.titulo || "",
            autor: libroExterno.autor || "",
            editorial: libroExterno.editorial || "",
          });
          setOrigen("externo");
          setDatosCargados(true);
          actions.setMensaje(
            `Datos obtenidos de ${libroExterno.fuente || "fuente externa"}. Puede editar si es necesario.`
          );
        } else {
          actions.setMensaje(
            "No se encontró información para este ISBN. Puede ingresar los datos manualmente."
          );
          setOrigen("");
          setDatosCargados(false);
          limpiarDatosLibro(isbn);
        }
      }
    } catch (error) {
      console.error("Error al autocompletar los datos:", error);
      actions.setMensaje("Hubo un error al buscar información del libro.");
    } finally {
      setIsLoading(false);
    }
  };

  const handleIsbnBlur = () => {
    if (formData.isbn && !datosCargados && !sinIsbn) {
      handleAutocomplete();
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const { isbn, titulo, autor, ubicacion, stock } = formData;

    if (!isbn || !titulo || !autor || !ubicacion) {
      alert("Por favor, complete los campos obligatorios: ISBN, título, autor y ubicación.");
      return;
    }

    if (stock < 1) {
      alert("El stock debe ser al menos 1.");
      return;
    }

    if (formData.precio < 0) {
      alert("El precio no puede ser negativo.");
      return;
    }

    try {
      const libroExistente = await actions.buscarLibroPorISBN(formData.isbn);

      if (libroExistente) {
        const resultado = await actions.actualizarLibro(libroExistente.id, formData);

        if (resultado.success) {
          actions.setMensaje("✅ Libro actualizado exitosamente.");
          setTimeout(() => actions.setMensaje(""), 10000);
        } else {
          alert(resultado.error || "Hubo un error al actualizar el libro");
        }
      } else {
        const resultado = await actions.crearLibro(formData);

        if (resultado.success) {
          actions.setMensaje(
            `✅ Libro creado con éxito con stock de ${formData.stock} unidad(es).`
          );
          setTimeout(() => actions.setMensaje(""), 10000);
          setFormData({ isbn: "", titulo: "", autor: "", editorial: "", stock: 1, precio: 0, ubicacion: "" });
          setOrigen("");
          setDatosCargados(false);
          setSinIsbn(false);
          setIsbnGenerado("");
        } else {
          alert(resultado.error || "Hubo un error al crear el libro");
        }
      }
    } catch (error) {
      console.error("Error en la solicitud:", error);
      alert("Hubo un error con la solicitud: " + error.message);
    }
  };

  return (
    <div className="container my-4" style={{
      maxWidth: "800px",
      backgroundColor: "#e0f7e9",
      borderRadius: "12px",
      boxShadow: "0 8px 20px rgba(0,0,0,0.1)",
      padding: "30px 25px",
      fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif"
    }}>
      <div style={{
        position: "relative",
        display: "flex",
        alignItems: "center",
        marginBottom: "25px",
        height: "40px"
      }}>
        <button type="button" className="btn btn-secondary" onClick={() => navigate("/")} style={{
          borderRadius: "8px",
          fontWeight: "600",
          padding: "10px 20px",
          boxShadow: "0 4px 8px rgba(0,0,0,0.1)",
          transition: "background-color 0.3s ease",
          zIndex: 2
        }}>
          Volver al Inicio
        </button>
        <h2 style={{
          position: "absolute",
          left: "50%",
          transform: "translateX(-50%)",
          color: "#1b7445",
          fontWeight: "700",
          margin: 0,
          fontSize: "1.8rem",
          userSelect: "none",
          zIndex: 1
        }}>
          Crear Libro
        </h2>
      </div>
      <form onSubmit={handleSubmit}>
        {["isbn", "titulo", "autor", "editorial", "stock", "precio", "ubicacion"].map((field, idx) => (
          <div className="mb-3" key={field}>
            <label className="form-label" style={{ color: "#1b7445", fontWeight: "600" }}>{field.toUpperCase()}:</label>
            <input
              type={field === "stock" || field === "precio" ? "number" : "text"}
              name={field}
              value={formData[field]}
              onChange={handleChange}
              required={field !== "editorial" && field !== "precio"}
              style={{
                width: "100%",
                padding: "10px 15px",
                borderRadius: "8px",
                border: "1.5px solid #1b7445",
                backgroundColor: "#f0fff5",
                color: "#1b7445",
                fontWeight: "500",
                fontSize: "1rem",
                boxShadow: "inset 1px 1px 3px rgba(27,116,69,0.15)",
                transition: "border-color 0.3s ease"
              }}
            />
          </div>
        ))}
        <div className="d-flex gap-3 mb-3">
          <button type="submit" className="btn" style={{
            flex: 1,
            background: "linear-gradient(135deg, #42b883 0%, #1b7445 100%)",
            color: "white",
            fontWeight: "700",
            fontSize: "1.25rem",
            padding: "12px 0",
            borderRadius: "10px",
            boxShadow: "0 6px 12px rgba(27,116,69,0.5)",
            transition: "background 0.3s ease"
          }}>
            Guardar Libro
          </button>

          <button type="button" className="btn btn-warning" onClick={() => {
            setFormData({ isbn: "", titulo: "", autor: "", editorial: "", stock: 1, precio: 0, ubicacion: "" });
            setOrigen("");
            setDatosCargados(false);
            setSinIsbn(false);
            setIsbnGenerado("");
            actions.setMensaje("");
          }} style={{
            flex: 1,
            fontWeight: "700",
            fontSize: "1.25rem",
            borderRadius: "10px",
            boxShadow: "0 6px 12px rgba(184,136,50,0.5)",
            transition: "background-color 0.3s ease"
          }}>
            Limpiar Pantalla
          </button>
        </div>
        {mensaje && (
          <div className="mt-3" style={{ color: mensaje.includes("✅") ? "#2e7d32" : "#b22e2e", fontWeight: "700", fontSize: "1.1rem" }}>
            {mensaje}
          </div>
        )}
      </form>
    </div>
  );
};

export default AgregarLibro;
