import React, { useState } from "react";
import { useAppContext } from "../context/appContext"; // Importamos el contexto
import { useNavigate } from "react-router-dom";

const AgregarLibro = () => {
  const handleInputKeyDown = (e) => {
    if (e.key === "Enter") {
      e.preventDefault();
      const form = e.target.form;
      const index = Array.prototype.indexOf.call(form, e.target);
      if (form.elements[index + 1]) {
        form.elements[index + 1].focus();
      }
    }
  };

  const { store, actions } = useAppContext();
  const navigate = useNavigate();

  const [formData, setFormData] = useState({
    isbn: "",
    titulo: "",
    autor: "",
    editorial: "",
    stock: 1,
    precio: 0,
    ubicacion: "",
  });

  const [sinIsbn, setSinIsbn] = useState(false);
  const limpiarDatosLibro = (isbnValue = formData.isbn) => {
    setFormData({
      isbn: isbnValue,
      titulo: "",
      autor: "",
      editorial: "",
      stock: 1,
      precio: 0,
      ubicacion: "",
    });
  };

  const [isLoading, setIsLoading] = useState(false);
  const [datosCargados, setDatosCargados] = useState(false);
  const [origen, setOrigen] = useState("");

  const mensaje = store.mensaje;

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });

    if (name === "isbn") {
      setOrigen("");
      setDatosCargados(false);
      limpiarDatosLibro(value);
    }

    actions.setMensaje("");
  };

  const obtenerIsbnGenerado = async () => {
    try {
      const res = await fetch("http://localhost:5000/generar-isbn");
      const data = await res.json();
      return data.isbn;
    } catch (error) {
      console.error("Error al generar ISBN:", error);
      return null;
    }
  };

  const handleAutocomplete = async () => {
    const { isbn } = formData;
    if (!isbn) return;
    setIsLoading(true);
    setOrigen("");
    try {
      const libroLocal = await actions.buscarLibroPorISBN(isbn);
      if (libroLocal) {
        setFormData({
          ...formData,
          autor: libroLocal.autor || "",
          editorial: libroLocal.editorial || "",
          stock: libroLocal.stock || 1,
          precio: libroLocal.precio || 0,
          titulo: libroLocal.titulo || "",
          ubicacion: libroLocal.ubicacion || "",
        });
        setOrigen("local");
        setDatosCargados(true);
      } else {
        const libroExterno = await actions.buscarLibroExterno(isbn);
        if (libroExterno) {
          setFormData({
            ...formData,
            titulo: libroExterno.titulo || "",
            autor: libroExterno.autor || "",
            editorial: libroExterno.editorial || "",
          });
          setOrigen("externo");
          setDatosCargados(true);
          actions.setMensaje(`Datos obtenidos de ${libroExterno.fuente || "fuente externa"}. Puede editar si es necesario.`);
        } else {
          actions.setMensaje("No se encontró información para este ISBN. Puede ingresar los datos manualmente.");
          setOrigen("");
          setDatosCargados(false);
          limpiarDatosLibro(isbn);
        }
      }
    } catch (error) {
      console.error("Error al autocompletar los datos:", error);
      actions.setMensaje("Hubo un error al buscar información del libro.");
    } finally {
      setIsLoading(false);
    }
  };

  const handleIsbnBlur = () => {
    if (formData.isbn && !datosCargados) {
      handleAutocomplete();
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!formData.titulo || !formData.autor) {
      alert("Por favor, complete todos los campos obligatorios.");
      return;
    }

    if (formData.stock < 1) {
      alert("El stock debe ser de al menos 1 unidad.");
      return;
    }

    if (formData.precio < 0) {
      alert("El precio no puede ser negativo.");
      return;
    }

    try {
      if (sinIsbn && !formData.isbn) {
        const nuevoIsbn = await obtenerIsbnGenerado();
        if (nuevoIsbn) {
          formData.isbn = nuevoIsbn;
        } else {
          alert("Error al generar ISBN");
          return;
        }
      }

      const libroExistente = await actions.buscarLibroPorISBN(formData.isbn);

      if (libroExistente) {
        let cambios = [];
        if (formData.titulo !== libroExistente.titulo) cambios.push("título");
        if (formData.autor !== libroExistente.autor) cambios.push("autor");
        if (formData.editorial !== libroExistente.editorial) cambios.push("editorial");
        if (parseInt(formData.stock) !== parseInt(libroExistente.stock)) cambios.push(`stock (${libroExistente.stock} → ${formData.stock})`);
        if (parseFloat(formData.precio) !== parseFloat(libroExistente.precio)) cambios.push("precio");
        if (formData.ubicacion !== libroExistente.ubicacion) cambios.push("ubicación");

        if (cambios.length > 0) {
          const resultado = await actions.actualizarLibro(libroExistente.id, formData);
          if (resultado.success) {
            actions.setMensaje(`Libro actualizado con éxito. Campos modificados: ${cambios.join(", ")}.`);
            setOrigen("local");
          } else {
            alert(resultado.error || "Hubo un error al actualizar el libro");
          }
        } else {
          actions.setMensaje("No se realizaron cambios en el libro.");
        }
      } else {
        const resultado = await actions.crearLibro(formData);
        if (resultado.success) {
          actions.setMensaje(`Libro creado con éxito con stock de ${formData.stock} unidad(es).`);
          setFormData({ isbn: "", titulo: "", autor: "", editorial: "", stock: 1, precio: 0, ubicacion: "" });
          setOrigen("");
          setDatosCargados(false);
          setSinIsbn(false);
        } else {
          alert(resultado.error || "Hubo un error al crear el libro");
        }
      }
    } catch (error) {
      console.error("Error en la solicitud:", error);
      alert("Hubo un error con la solicitud: " + error.message);
    }
  };

  return (
    <div className="container mt-5 bg-success">
      <div className="card shadow-lg p-4">
        <div className="mb-3">
          <button type="button" className="btn btn-secondary" onClick={() => navigate("/")}>Volver al inicio</button>
        </div>
        <h2 className="mb-4 text-center">Crear Nuevo Libro</h2>
        {mensaje && <div className="alert alert-info">{mensaje}</div>}

        <form onSubmit={handleSubmit}>
          <div className="mb-3">
            <label className="form-label">
              <input
                type="checkbox"
                className="form-check-input me-2"
                checked={sinIsbn}
                onChange={async (e) => {
                  const checked = e.target.checked;
                  setSinIsbn(checked);
                  if (checked) {
                    const nuevoIsbn = await obtenerIsbnGenerado();
                    if (nuevoIsbn) {
                      setFormData((prevData) => ({ ...prevData, isbn: nuevoIsbn }));
                      setOrigen("generado");
                      setDatosCargados(true);
                      actions.setMensaje("ISBN generado automáticamente.");
                    } else {
                      alert("No se pudo generar el ISBN automáticamente.");
                      setSinIsbn(false);
                    }
                  } else {
                    setFormData((prevData) => ({ ...prevData, isbn: "" }));
                    setOrigen("");
                    setDatosCargados(false);
                    actions.setMensaje("");
                  }
                }}
              />
              Crear sin ISBN (generar automáticamente)
            </label>
          </div>

          <div className="mb-3">
            <label htmlFor="isbn" className="form-label">ISBN:</label>
            <div className="input-group">
              <input
                type="text"
                className="form-control"
                id="isbn"
                name="isbn"
                value={formData.isbn}
                onChange={handleChange}
                onBlur={handleIsbnBlur}
                required
                disabled={sinIsbn}
                onKeyDown={handleInputKeyDown}
              />
              {isLoading && (
                <span className="input-group-text">
                  <div className="spinner-border spinner-border-sm" role="status">
                    <span className="visually-hidden">Cargando...</span>
                  </div>
                </span>
              )}
              {!isLoading && origen && (
                <span className={`input-group-text ${origen === "local"
                    ? "bg-success text-white"
                    : origen === "generado"
                      ? "bg-warning text-dark"
                      : "bg-info text-white"
                  }`}>
                  {origen === "local" ? "BD Local" : origen === "generado" ? "Generado" : "API Externa"}
                </span>
              )}
            </div>
            <small className="text-muted">
              {sinIsbn ? "ISBN generado automáticamente para este libro." : "Al ingresar el ISBN, se intentará autocompletar la información del libro."}
            </small>
          </div>

          <div className="mb-3">
            <label htmlFor="titulo" className="form-label">Título:</label>
            <input type="text" className="form-control" id="titulo" name="titulo" value={formData.titulo} onChange={handleChange} required onKeyDown={handleInputKeyDown} />
          </div>

          <div className="mb-3">
            <label htmlFor="autor" className="form-label">Autor:</label>
            <input type="text" className="form-control" id="autor" name="autor" value={formData.autor} onChange={handleChange} required onKeyDown={handleInputKeyDown} />
          </div>

          <div className="mb-3">
            <label htmlFor="editorial" className="form-label">Editorial:</label>
            <input type="text" className="form-control" id="editorial" name="editorial" value={formData.editorial} onChange={handleChange} onKeyDown={handleInputKeyDown} />
          </div>

          <div className="mb-3">
            <label htmlFor="stock" className="form-label">Stock (mínimo 1):</label>
            <input type="number" className="form-control" id="stock" name="stock" value={formData.stock} onChange={handleChange} min="1" onKeyDown={handleInputKeyDown} />
          </div>

          <div className="mb-3">
            <label htmlFor="precio" className="form-label">Precio:</label>
            <input type="number" className="form-control" id="precio" name="precio" value={formData.precio} onChange={handleChange} onKeyDown={handleInputKeyDown} />
          </div>

          <div className="mb-3">
            <label htmlFor="ubicacion" className="form-label">Ubicación:</label>
            <input type="text" className="form-control" id="ubicacion" name="ubicacion" value={formData.ubicacion} onChange={handleChange} onKeyDown={handleInputKeyDown} />
          </div>

          <div className="row mt-4">
            <div className="col-9">
              <button type="submit" className="btn btn-success btn-lg w-100">
                {formData.id ? "Actualizar Libro" : "Crear Libro"}
              </button>
            </div>
            <div className="col-3">
              <button type="button" className="btn btn-warning btn-lg w-100" onClick={() => {
                setFormData({ isbn: "", titulo: "", autor: "", editorial: "", stock: 1, precio: 0, ubicacion: "" });
                setOrigen("");
                setDatosCargados(false);
                setSinIsbn(false);
                actions.setMensaje("");
              }}>
                Refrescar
              </button>
            </div>
          </div>
        </form>
      </div>
    </div>
  );
};

export default AgregarLibro;
