import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { useAppContext } from "../context/appContext"; // Ajusta la ruta según tu estructura

const BuscarLibro = () => {
  const navigate = useNavigate();
  const { actions } = useAppContext();

  const [formData, setFormData] = useState({
    isbn: "",
    titulo: "",
    autor: "",
    editorial: "",
    stock: "",
    ubicacion: "",
  });
  const [resultados, setResultados] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");
  const [autocompleteLoading, setAutocompleteLoading] = useState(false);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  // Función para autocompletar datos desde API externa cuando se introduce el ISBN
  const handleIsbnBlur = async () => {
    if (!formData.isbn || formData.isbn.length < 10) return;

    setAutocompleteLoading(true);
    setError("");

    try {
      // Primero buscamos en nuestra base de datos
      const libroLocal = await actions.buscarLibroPorISBN(formData.isbn);

      if (libroLocal) {
        // Si existe en nuestra BD, usamos esos datos
        setFormData({
          ...formData,
          titulo: libroLocal.titulo,
          autor: libroLocal.autor,
          editorial: libroLocal.editorial || "",
          stock: libroLocal.stock,
          ubicacion: libroLocal.ubicacion || "",
        });
      } else {
        // Si no existe, buscamos en la API externa
        const libroExterno = await actions.buscarLibroExterno(formData.isbn);

        if (libroExterno) {
          setFormData({
            ...formData,
            titulo: libroExterno.titulo,
            autor: libroExterno.autor,
            editorial: libroExterno.editorial || "",
          });
        } else {
          // No se encontró en ninguna parte
          setError(
            "No se encontró información para este ISBN. Puede ingresar los datos manualmente."
          );
        }
      }
    } catch (err) {
      console.error("Error al autocompletar datos:", err);
      setError("Error al buscar información del libro.");
    } finally {
      setAutocompleteLoading(false);
    }
  };

  const handleSearch = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError("");
    setResultados([]);

    try {
      let filtro = {};

      // Agregamos al filtro solo los campos que tengan valor
      if (formData.isbn) filtro.isbn = formData.isbn;
      if (formData.titulo) filtro.titulo = formData.titulo;
      if (formData.autor) filtro.autor = formData.autor;
      if (formData.editorial) filtro.editorial = formData.editorial;

      // Aquí necesitarías implementar una función en tu contexto para buscar con filtros
      // Por ahora usaremos la búsqueda por ISBN si está disponible, o mostraremos todos los libros
      let librosEncontrados = [];

      if (formData.isbn) {
        const libro = await actions.buscarLibroPorISBN(formData.isbn);
        if (libro) librosEncontrados = [libro];
      } else {
        // Asumiendo que tienes todos los libros en el store del contexto
        const { store } = useAppContext();
        librosEncontrados = store.libros.filter((libro) => {
          let coincide = true;

          if (
            filtro.titulo &&
            !libro.titulo.toLowerCase().includes(filtro.titulo.toLowerCase())
          )
            coincide = false;
          if (
            filtro.autor &&
            !libro.autor.toLowerCase().includes(filtro.autor.toLowerCase())
          )
            coincide = false;
          if (
            filtro.editorial &&
            !libro.editorial
              ?.toLowerCase()
              .includes(filtro.editorial.toLowerCase())
          )
            coincide = false;

          return coincide;
        });
      }

      if (librosEncontrados.length > 0) {
        setResultados(librosEncontrados);
      } else {
        setError("No se encontraron libros con los criterios especificados");
      }
    } catch (err) {
      console.error("Error al buscar libros:", err);
      setError("Error al realizar la búsqueda");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="container mt-5">
      <div className="card shadow p-4">
        <h2 className="mb-4 text-center">Buscar Libro</h2>

        <form onSubmit={handleSearch}>
          <div className="mb-3">
            <label className="form-label">ISBN:</label>
            <div className="input-group">
              <input
                type="text"
                className="form-control"
                name="isbn"
                value={formData.isbn}
                onChange={handleChange}
                onBlur={handleIsbnBlur}
                placeholder="Ingrese el ISBN del libro"
              />
              {autocompleteLoading && (
                <span className="input-group-text">
                  <div
                    className="spinner-border spinner-border-sm"
                    role="status"
                  >
                    <span className="visually-hidden">Cargando...</span>
                  </div>
                </span>
              )}
            </div>
            <small className="text-muted">
              Al ingresar un ISBN válido, se autocompletarán los datos si están
              disponibles
            </small>
          </div>

          <div className="mb-3">
            <label className="form-label">Título:</label>
            <input
              type="text"
              className="form-control"
              name="titulo"
              value={formData.titulo}
              onChange={handleChange}
              placeholder="Ingrese el título del libro"
            />
          </div>

          <div className="mb-3">
            <label className="form-label">Autor:</label>
            <input
              type="text"
              className="form-control"
              name="autor"
              value={formData.autor}
              onChange={handleChange}
              placeholder="Ingrese el autor del libro"
            />
          </div>

          <div className="mb-3">
            <label className="form-label">Editorial:</label>
            <input
              type="text"
              className="form-control"
              name="editorial"
              value={formData.editorial}
              onChange={handleChange}
              placeholder="Ingrese la editorial"
            />
          </div>

          {error && <div className="alert alert-warning">{error}</div>}

          <div className="d-flex justify-content-between mb-3">
            <button
              type="submit"
              className="btn btn-primary"
              disabled={loading}
            >
              {loading ? (
                <>
                  <span
                    className="spinner-border spinner-border-sm me-2"
                    role="status"
                    aria-hidden="true"
                  ></span>
                  Buscando...
                </>
              ) : (
                "Buscar"
              )}
            </button>
            <button
              type="button"
              className="btn btn-secondary"
              onClick={() => navigate("/")}
            >
              Volver al Inicio
            </button>
          </div>
        </form>

        {resultados.length > 0 && (
          <div className="mt-4">
            <h4>Resultados:</h4>
            <div className="table-responsive">
              <table className="table table-striped">
                <thead>
                  <tr>
                    <th>ISBN</th>
                    <th>Título</th>
                    <th>Autor</th>
                    <th>Editorial</th>
                    <th>Stock</th>
                    <th>Ubicación</th>
                    <th>Acciones</th>
                  </tr>
                </thead>
                <tbody>
                  {resultados.map((libro) => (
                    <tr key={libro.id}>
                      <td>{libro.isbn}</td>
                      <td>{libro.titulo}</td>
                      <td>{libro.autor}</td>
                      <td>{libro.editorial || "-"}</td>
                      <td>{libro.stock}</td>
                      <td>{libro.ubicacion || "-"}</td>
                      <td>
                        <button
                          className="btn btn-sm btn-info me-1"
                          onClick={() => navigate(`/editar-libro/${libro.id}`)}
                        >
                          Editar
                        </button>
                        <button
                          className="btn btn-sm btn-warning"
                          onClick={() => {
                            setFormData({
                              isbn: libro.isbn,
                              titulo: libro.titulo,
                              autor: libro.autor,
                              editorial: libro.editorial || "",
                              stock: libro.stock,
                              ubicacion: libro.ubicacion || "",
                            });
                            navigate(`/bajar-libro`, { state: { libro } });
                          }}
                        >
                          Bajar Stock
                        </button>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default BuscarLibro;
