import React, { useState, useEffect } from "react";
import { useAppContext } from "../context/appContext";
import { useNavigate } from "react-router-dom";

const AgregarLibro = () => {
  const { store, actions } = useAppContext();
  const navigate = useNavigate();

  // Autoincremental sin ISBN
  const [usarSinIsbn, setUsarSinIsbn] = useState(false); // NUEVO

  const [formData, setFormData] = useState({
    isbn: "",
    titulo: "",
    autor: "",
    editorial: "",
    stock: 1,
    precio: 0,
    ubicacion: "",
  });

  const [isLoading, setIsLoading] = useState(false);
  const [datosCargados, setDatosCargados] = useState(false);
  const [origen, setOrigen] = useState("");

  const mensaje = store.mensaje;

  useEffect(() => {
    if (usarSinIsbn) {
      // Obtener último número usado desde localStorage
      const ultimo = parseInt(localStorage.getItem("ultimoIsbnSinCodigo") || "0");
      const nuevo = ultimo + 1;
      localStorage.setItem("ultimoIsbnSinCodigo", nuevo);
      setFormData((prev) => ({
        ...prev,
        isbn: `SINISBN-${nuevo}`, // Prefijo para que no choque con un ISBN real
      }));
    } else {
      setFormData((prev) => ({ ...prev, isbn: "" }));
    }
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [usarSinIsbn]); // NUEVO: se ejecuta solo cuando se marca/desmarca el checkbox

  const handleInputKeyDown = (e) => {
    if (e.key === "Enter") {
      e.preventDefault();
      const form = e.target.form;
      const index = Array.prototype.indexOf.call(form, e.target);
      if (form.elements[index + 1]) {
        form.elements[index + 1].focus();
      }
    }
  };

  const limpiarDatosLibro = (isbnValue = formData.isbn) => {
    setFormData({
      isbn: isbnValue,
      titulo: "",
      autor: "",
      editorial: "",
      stock: 1,
      precio: 0,
      ubicacion: "",
    });
  };

  const handleChange = (e) => {
    const { name, value } = e.target;

    if (name === "isbn" && usarSinIsbn) return; // Evita que modifique si está tildado "sin ISBN"

    setFormData({
      ...formData,
      [name]: value,
    });

    if (name === "isbn") {
      setOrigen("");
      setDatosCargados(false);
      limpiarDatosLibro(value);
    }

    actions.setMensaje("");
  };

  const handleAutocomplete = async () => {
    const { isbn } = formData;

    if (!isbn || usarSinIsbn) return; // No autocompleta si es sin ISBN

    setIsLoading(true);
    setOrigen("");

    try {
      const libroLocal = await actions.buscarLibroPorISBN(isbn);

      if (libroLocal) {
        setFormData({
          ...formData,
          autor: libroLocal.autor || "",
          editorial: libroLocal.editorial || "",
          stock: libroLocal.stock || 1,
          precio: libroLocal.precio || 0,
          titulo: libroLocal.titulo || "",
          ubicacion: libroLocal.ubicacion || "",
        });
        setOrigen("local");
        setDatosCargados(true);
      } else {
        const libroExterno = await actions.buscarLibroExterno(isbn);

        if (libroExterno) {
          setFormData({
            ...formData,
            titulo: libroExterno.titulo || "",
            autor: libroExterno.autor || "",
            editorial: libroExterno.editorial || "",
          });
          setOrigen("externo");
          setDatosCargados(true);
          actions.setMensaje(
            `Datos obtenidos de ${libroExterno.fuente || "fuente externa"
            }. Puede editar si es necesario.`
          );
        } else {
          actions.setMensaje(
            "No se encontró información para este ISBN. Puede ingresar los datos manualmente."
          );
          setOrigen("");
          setDatosCargados(false);
          limpiarDatosLibro(isbn);
        }
      }
    } catch (error) {
      console.error("Error al autocompletar los datos:", error);
      actions.setMensaje("Hubo un error al buscar información del libro.");
    } finally {
      setIsLoading(false);
    }
  };

  const handleIsbnBlur = () => {
    if (formData.isbn && !datosCargados && !usarSinIsbn) {
      handleAutocomplete();
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!formData.isbn || !formData.titulo || !formData.autor) {
      alert("Por favor, complete todos los campos obligatorios.");
      return;
    }

    if (formData.stock < 1) {
      alert("El stock debe ser de al menos 1 unidad.");
      return;
    }

    if (formData.precio < 0) {
      alert("El precio no puede ser negativo.");
      return;
    }

    try {
      const libroExistente = await actions.buscarLibroPorISBN(formData.isbn);

      if (libroExistente) {
        let cambios = [];

        if (formData.titulo !== libroExistente.titulo) cambios.push("título");
        if (formData.autor !== libroExistente.autor) cambios.push("autor");
        if (formData.editorial !== libroExistente.editorial) cambios.push("editorial");
        if (parseInt(formData.stock) !== parseInt(libroExistente.stock)) cambios.push("stock");
        if (parseFloat(formData.precio) !== parseFloat(libroExistente.precio)) cambios.push("precio");
        if (formData.ubicacion !== libroExistente.ubicacion) cambios.push("ubicación");

        if (cambios.length > 0) {
          const resultado = await actions.actualizarLibro(libroExistente.id, formData);
          if (resultado.success) {
            actions.setMensaje(`Libro actualizado. Campos modificados: ${cambios.join(", ")}`);
            setOrigen("local");
          } else {
            alert(resultado.error || "Error al actualizar el libro");
          }
        } else {
          actions.setMensaje("No se realizaron cambios en el libro.");
        }
      } else {
        const resultado = await actions.crearLibro(formData);

        if (resultado.success) {
          actions.setMensaje(`Libro creado con éxito con stock de ${formData.stock}.`);
          setFormData({
            isbn: "",
            titulo: "",
            autor: "",
            editorial: "",
            stock: 1,
            precio: 0,
            ubicacion: "",
          });
          setOrigen("");
          setDatosCargados(false);
          setUsarSinIsbn(false); // Reinicia checkbox
        } else {
          alert(resultado.error || "Error al crear el libro");
        }
      }
    } catch (error) {
      console.error("Error en la solicitud:", error);
      alert("Error: " + error.message);
    }
  };

  return (
    <div className="container mt-5 bg-success">
      <div className="card shadow-lg p-4">
        <div className="mb-3">
          <button type="button" className="btn btn-secondary" onClick={() => navigate("/")}>
            Volver al inicio
          </button>
        </div>
        <h2 className="mb-4 text-center">Crear Nuevo Libro</h2>
        {mensaje && <div className="alert alert-info">{mensaje}</div>}

        <form onSubmit={handleSubmit}>
          <div className="mb-3">
            <label htmlFor="isbn" className="form-label">ISBN:</label>
            <div className="input-group">
              <input
                type="text"
                className="form-control"
                id="isbn"
                name="isbn"
                value={formData.isbn}
                onChange={handleChange}
                onBlur={handleIsbnBlur}
                disabled={usarSinIsbn} // NUEVO
                required
                onKeyDown={handleInputKeyDown}
              />
              {isLoading && (
                <span className="input-group-text">
                  <div className="spinner-border spinner-border-sm" role="status">
                    <span className="visually-hidden">Cargando...</span>
                  </div>
                </span>
              )}
              {!isLoading && origen && (
                <span
                  className={`input-group-text ${origen === "local" ? "bg-success text-white" : "bg-info text-white"
                    }`}
                >
                  {origen === "local" ? "BD Local" : "API Externa"}
                </span>
              )}
            </div>
            <div className="form-check mt-2"> {/* NUEVO */}
              <input
                className="form-check-input"
                type="checkbox"
                id="sinIsbn"
                checked={usarSinIsbn}
                onChange={(e) => setUsarSinIsbn(e.target.checked)}
              />
              <label className="form-check-label" htmlFor="sinIsbn">
                Sin ISBN (autogenerado)
              </label>
            </div>
            <small className="text-muted">
              Al ingresar el ISBN, se intentará autocompletar la información del libro.
            </small>
          </div>

          {/* Resto del formulario sin cambios... */}
          {/* Campo Título, Autor, Editorial, Stock, Precio, Ubicación, Botones */}

          {/* ... */}
        </form>
      </div>
    </div>
  );
};

export default AgregarLibro;
